window.onload=function(){var a=document.getElementById("canvas");var k=a.getContext("2d");var c=window.innerWidth;var h=window.innerHeight-100;a.width=c;a.height=h;var b=500;var j=[];for(var f=0;f<b;f++){j.push({x:Math.random()*c,y:Math.random()*h,r:Math.random()*3+1,d:Math.random()*b})}function g(){k.clearRect(0,0,c,h);k.fillStyle="rgba(255, 255, 255, 0.8)";k.beginPath();for(var l=0;l<b;l++){var m=j[l];k.moveTo(m.x,m.y);k.arc(m.x,m.y,m.r,0,Math.PI*2,true)}k.fill();e()}var d=0;function e(){for(var l=0;l<b;l++){var m=j[l];m.y+=Math.cos(d+m.d)+1+m.r/2;m.x+=Math.sin(d)*2;if(m.x>c||m.x<0||m.y>h){if(l%3>0){j[l]={x:Math.random()*c,y:-10,r:m.r,d:m.d}}else{if(Math.sin(d)>0){j[l]={x:-5,y:Math.random()*h,r:m.r,d:m.d}}else{j[l]={x:c+5,y:Math.random()*h,r:m.r,d:m.d}}}}}}setInterval(g,30)};